methods.onClick:
async function(event, $m) {
  try {
    // Access the camera using mediaUtils
    const constraints = {
      video: {
        facingMode: 'user',
        width: { ideal: 1280 },
        height: { ideal: 720 }
      },
      audio: false
    };

    const stream = await navigator.mediaDevices.getUserMedia(constraints);

    // Get the video element and set the stream
    const video = document.getElementById('video-element');
    if (video) {
      video.srcObject = stream;
      video.play();
      $m('#status-text').setValue('Camera active');
    }
  } catch (error) {
    console.error('Error accessing camera:', error);
    $m('#status-text').setValue('Error: ' + error.message);
  }
}

function(event, $m) {
  const video = document.getElementById('video-element');
  if (video && video.srcObject) {
    // Stop all tracks in the stream
    const stream = video.srcObject;
    if (stream instanceof MediaStream) {
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop());
    }

    // Clear the source
    video.srcObject = null;
    video.pause();

    // Clear the canvas overlay
    const canvas = document.getElementById('overlay-canvas');
    if (canvas) {
      const ctx = canvas.getContext('2d');
      if (ctx) {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
      }
    }

    $m('#status-text').setValue('Camera stopped');
  }
}

function(event, $m) {
  // Get the video element
  const video = document.getElementById('video-element');
  if (!video || video.paused || video.ended) {
    $m('#status-text').setValue('No active video stream to capture');
    return;
  }

  try {
    // Create a canvas to capture the image
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Draw the current video frame to the canvas
    const ctx = canvas.getContext('2d');
    if (ctx) {
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Convert to data URL
      const imageDataUrl = canvas.toDataURL('image/jpeg', 0.9);

      // Store the captured image URL
      $m('#captured-image').setProperty('src', imageDataUrl);

      // Show the captured image
      $m('#captured-image').setStyle('display', 'block');
      $m('#status-text').setValue('Image captured');
    }
  } catch (error) {
    console.error('Error capturing image:', error);
    $m('#status-text').setValue('Error capturing image');
  }
}

function(event, $m) {
  // Get the video element and overlay canvas
  const video = document.getElementById('video-element');
  const canvas = document.getElementById('overlay-canvas');

  if (!video || !canvas) return;

  // Get the 2D context of the canvas
  const ctx = canvas.getContext('2d');
  if (!ctx) return;

  // Make sure the canvas dimensions match the video dimensions
  const rect = video.getBoundingClientRect();
  canvas.width = rect.width;
  canvas.height = rect.height;

  // Clear previous drawings
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // For demonstration, we'll create a detection zone in the center
  const detectionWidth = canvas.width / 3;
  const detectionHeight = canvas.height / 3;
  const x = (canvas.width - detectionWidth) / 2;
  const y = (canvas.height - detectionHeight) / 2;

  // Draw detection rectangle
  ctx.strokeStyle = 'rgba(46, 204, 113, 0.8)';
  ctx.lineWidth = 3;
  ctx.strokeRect(x, y, detectionWidth, detectionHeight);

  // Add a label
  ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
  ctx.fillRect(x, y - 30, 140, 25);
  ctx.fillStyle = 'white';
  ctx.font = '16px Arial';
  ctx.fillText('Detection Region', x + 10, y - 12);

  $m('#status-text').setValue('Analysis complete');
  $m('#analysis-result').setValue('Object detected at center of frame (demonstration)');
}

function(event, $m) {
  const canvas = document.getElementById('overlay-canvas');
  if (canvas) {
    const ctx = canvas.getContext('2d');
    if (ctx) {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      $m('#status-text').setValue('Overlay cleared');
    }
  }
}

function(event, $m) {
  const video = document.getElementById('video-element');
  const canvas = document.getElementById('overlay-canvas');

  if (!video || !canvas) return;

  const ctx = canvas.getContext('2d');
  if (!ctx) return;

  // Set canvas dimensions
  canvas.width = video.videoWidth || video.clientWidth;
  canvas.height = video.videoHeight || video.clientHeight;

  // Draw the current video frame
  ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

  // Get the image data
  const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
  const data = imageData.data;

  // Apply grayscale filter
  for (let i = 0; i < data.length; i += 4) {
    const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
    data[i] = data[i + 1] = data[i + 2] = avg;
  }

  // Put the modified image data back
  ctx.putImageData(imageData, 0, 0);

  $m('#status-text').setValue('Grayscale filter applied');
}

function(event, $m) {
  const video = document.getElementById('video-element');
  const canvas = document.getElementById('overlay-canvas');

  if (!video || !canvas) return;

  const ctx = canvas.getContext('2d');
  if (!ctx) return;

  // Set canvas dimensions
  canvas.width = video.videoWidth || video.clientWidth;
  canvas.height = video.videoHeight || video.clientHeight;

  // Draw the current video frame
  ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

  // Get the image data
  const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
  const data = imageData.data;

  // Apply invert filter
  for (let i = 0; i < data.length; i += 4) {
    data[i] = 255 - data[i];         // red
    data[i + 1] = 255 - data[i + 1]; // green
    data[i + 2] = 255 - data[i + 2]; // blue
  }

  // Put the modified image data back
  ctx.putImageData(imageData, 0, 0);

  $m('#status-text').setValue('Colors inverted');
}

function(event, $m) {
  const video = document.getElementById('video-element');
  const canvas = document.getElementById('overlay-canvas');

  if (!video || !canvas) return;

  const ctx = canvas.getContext('2d');
  if (!ctx) return;

  // Set canvas dimensions
  canvas.width = video.videoWidth || video.clientWidth;
  canvas.height = video.videoHeight || video.clientHeight;

  // Draw the current video frame
  ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

  // Get the image data
  const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
  const data = imageData.data;
  const width = canvas.width;
  const height = canvas.height;

  // Create a copy of the image data
  const origData = new Uint8ClampedArray(data);

  // Simple edge detection using a convolution
  for (let y = 1; y < height - 1; y++) {
    for (let x = 1; x < width - 1; x++) {
      const idx = (y * width + x) * 4;

      // Get brightness of surrounding pixels
      const tl = (origData[((y-1) * width + (x-1)) * 4] + 
                  origData[((y-1) * width + (x-1)) * 4 + 1] + 
                  origData[((y-1) * width + (x-1)) * 4 + 2]) / 3;

      const tr = (origData[((y-1) * width + (x+1)) * 4] + 
                  origData[((y-1) * width + (x+1)) * 4 + 1] + 
                  origData[((y-1) * width + (x+1)) * 4 + 2]) / 3;

      const bl = (origData[((y+1) * width + (x-1)) * 4] + 
                  origData[((y+1) * width + (x-1)) * 4 + 1] + 
                  origData[((y+1) * width + (x-1)) * 4 + 2]) / 3;

      const br = (origData[((y+1) * width + (x+1)) * 4] + 
                  origData[((y+1) * width + (x+1)) * 4 + 1] + 
                  origData[((y+1) * width + (x+1)) * 4 + 2]) / 3;

      // Calculate edge intensity with a simple gradient
      const edge = Math.abs(tl - br) + Math.abs(tr - bl);

      // Set edge to white if above threshold, otherwise black
      const threshold = 30;
      const val = edge > threshold ? 255 : 0;

      data[idx] = val;     // Red
      data[idx + 1] = val; // Green
      data[idx + 2] = val; // Blue
    }
  }

  // Put the modified image data back
  ctx.putImageData(imageData, 0, 0);

  $m('#status-text').setValue('Edge detection applied');
}

methods.onMount:
function($m) {
  // Wait for the DOM to be fully loaded
  setTimeout(() => {
    // Set initial status
    $m('#status-text').setValue('Ready - Click "Start Camera" to begin');

    // Check for camera support
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      $m('#status-text').setValue('Error: Camera not supported by this browser');
      $m('#start-camera-btn').setProperty('disabled', 'true');
    }

    // Add event listener for when the page is closed
    window.addEventListener('beforeunload', () => {
      // Stop any active camera stream
      const video = document.getElementById('video-element');
      if (video && video.srcObject) {
        const stream = video.srcObject;
        if (stream instanceof MediaStream) {
          const tracks = stream.getTracks();
          tracks.forEach(track => track.stop());
        }
      }
    });
  }, 500);
}
