methods.initialize:
function(event) { try { console.log('Initializing Barcelona Map application...'); // ===== Load Leaflet CSS and JS dependencies ===== // Add Leaflet CSS const leafletCss = document.createElement('link'); leafletCss.rel = 'stylesheet'; leafletCss.href = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.css'; leafletCss.integrity = 'sha256-p4NxAoJBhIIN+hmNHrzRCf9tD/miZyoHS5obTRR9BMY='; leafletCss.crossOrigin = ''; document.head.appendChild(leafletCss); // Load Leaflet JS const loadLeaflet = () => { return new Promise((resolve, reject) => { const leafletJs = document.createElement('script'); leafletJs.src = 'https://unpkg.com/leaflet@1.9.4/dist/leaflet.js'; leafletJs.integrity = 'sha256-20nQCchB9co0qIjJZRGuk2/Z9VM+kNiyxNV1lvTlZBo='; leafletJs.crossOrigin = ''; leafletJs.onload = resolve; leafletJs.onerror = reject; document.head.appendChild(leafletJs); }); }; // Initialize map function after dependencies are loaded const initMap = async () => { try { await loadLeaflet(); // Wait to ensure Leaflet is loaded // Default Barcelona coordinates const defaultPosition = { lat: 41.3851, lng: 2.1734, zoom: 13 }; // Initialize leaflet map window.morpheoMap = L.map('leaflet-map'); // Initialize markers array window.morpheoMarkers = []; window.morpheoMarkerMode = false; // Load previously saved state or use defaults const loadMapState = () => { try { const savedState = localStorage.getItem('morpheo-map-state'); if (savedState) { const state = JSON.parse(savedState); return { lat: state.lat || defaultPosition.lat, lng: state.lng || defaultPosition.lng, zoom: state.zoom || defaultPosition.zoom, markers: state.markers || [] }; } } catch (error) { console.error('Error loading map state:', error); } return defaultPosition; }; // Save map state to localStorage window.morpheoSaveMapState = () => { try { const center = window.morpheoMap.getCenter(); const zoom = window.morpheoMap.getZoom(); const markerData = window.morpheoMarkers.map(marker => ({ lat: marker.getLatLng().lat, lng: marker.getLatLng().lng, popup: marker.getPopup() ? marker.getPopup().getContent() : '' })); const state = { lat: center.lat, lng: center.lng, zoom: zoom, markers: markerData }; localStorage.setItem('morpheo-map-state', JSON.stringify(state)); updateMapStateInfo(state); } catch (error) { console.error('Error saving map state:', error); } }; // Update map state info display const updateMapStateInfo = (state) => { try { const stateInfo = document.getElementById('map-state-info'); if (!stateInfo) return; const markerCount = state.markers ? state.markers.length : 0; stateInfo.textContent = `Position: [${state.lat.toFixed(4)}, ${state.lng.toFixed(4)}]
Zoom: ${state.zoom}
Markers: ${markerCount}`; } catch (error) { console.error('Error updating map state info:', error); } }; // Initialize map with stored state or defaults const state = loadMapState(); window.morpheoMap.setView([state.lat, state.lng], state.zoom); // Add tile layer (OpenStreetMap) L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', { attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors', maxZoom: 19 }).addTo(window.morpheoMap); // Load saved markers if available if (state.markers && state.markers.length > 0) { state.markers.forEach(markerData => { const marker = L.marker([markerData.lat, markerData.lng]).addTo(window.morpheoMap); if (markerData.popup) { marker.bindPopup(markerData.popup); } window.morpheoMarkers.push(marker); }); } // Update state when map moves or zooms window.morpheoMap.on('moveend', window.morpheoSaveMapState); window.morpheoMap.on('zoomend', window.morpheoSaveMapState); // Initialize map control functions window.morpheoMapZoomIn = () => { window.morpheoMap.zoomIn(); }; window.morpheoMapZoomOut = () => { window.morpheoMap.zoomOut(); }; window.morpheoResetMapView = () => { window.morpheoMap.setView([defaultPosition.lat, defaultPosition.lng], defaultPosition.zoom); }; window.morpheoToggleMarkerMode = () => { window.morpheoMarkerMode = !window.morpheoMarkerMode; const addMarkerButton = document.getElementById('add-marker-button'); if (addMarkerButton) { addMarkerButton.style.backgroundColor = window.morpheoMarkerMode ? '#FF5722' : '#4CAF50'; } console.log('Marker mode ' + (window.morpheoMarkerMode ? 'enabled' : 'disabled')); }; window.morpheoClearMarkers = () => { window.morpheoMarkers.forEach(marker => { window.morpheoMap.removeLayer(marker); }); window.morpheoMarkers = []; window.morpheoSaveMapState(); }; // Click handler for adding markers window.morpheoMap.on('click', (e) => { if (window.morpheoMarkerMode) { const marker = L.marker(e.latlng).addTo(window.morpheoMap); // Prompt for popup content const popupContent = prompt('Enter description for this location (optional):', ''); if (popupContent) { marker.bindPopup(popupContent); } window.morpheoMarkers.push(marker); window.morpheoSaveMapState(); } }); // Initialize state display updateMapStateInfo(state); console.log('Map initialized successfully'); } catch (error) { console.error('Error initializing map:', error); const mapContainer = document.getElementById('leaflet-map'); if (mapContainer) { mapContainer.innerHTML = '<div style="padding: 20px; color: red;">Error loading map. Please try again later.</div>'; } } }; // Start initialization process initMap(); } catch(error) { console.error('Error in initialize method:', error); } }
